/**
 * Creates a new regular scroll listener
 *
 * @param {Element} observeElement
 * @param {Function} whenNext
 * @returns IntersectionObserver
 */
export function createScroller(observeElement: Element, whenNext: Function, reverse?: boolean): IntersectionObserver;
/**
 * Creates a new reverse scroll listener
 *
 * @param {Element} observeElement
 * @param {Function} whenNext
 * @returns IntersectionObserver
 */
export function createReverseScroller(observeElement: Element, whenNext: Function): IntersectionObserver;
